mapper.select:
select a.*,b.member_real_name as worker_name from work_order as a left join member as b on
a.order_accept_id=b.member_id where 1=1

	and order_is_delete=#{order_is_delete}


	and order_name like CONCAT('%',#{order_name},'%')


	and order_phone like CONCAT('%',#{order_phone},'%')


	and order_id like CONCAT('%',#{order_id},'%')


	and order_state=#{order_state}


	and order_create_time > #{order_create_time}


	and order_create_time < #{order_update_time}


	and find_in_set(order_state,#{stateList})


	and order_address_district=#{district}


	and is_complaints=#{is_complaints}

order by order_create_time desc

SELECT COUNT(*) AS all_count, ( select CASE WHEN SUM(CASE WHEN deposit_price is not null THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN deposit_price is not null THEN 1 ELSE 0 END) END FROM work_order WHERE order_is_delete='0'
and to_days(work_order.order_create_time) =to_days(now())
and YEARWEEK(date_format(work_order.order_create_time,'%Y-%m-%d')) = YEARWEEK(now())
and DATE_FORMAT(work_order.order_create_time, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' )
and YEAR(work_order.order_create_time) = YEAR( NOW( ) )
and work_order.order_create_time>#{start_time}
and work_order.order_create_time<#{end_time}
)AS work_order_deposit_count, ( select CASE WHEN SUM(CASE WHEN deposit_price is not null THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN deposit_price is not null THEN deposit_price ELSE 0 END) END FROM work_order WHERE order_is_delete='0'
and to_days(work_order.order_create_time) =to_days(now())
and YEARWEEK(date_format(work_order.order_create_time,'%Y-%m-%d')) = YEARWEEK(now())
and DATE_FORMAT(work_order.order_create_time, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' )
and YEAR(work_order.order_create_time) = YEAR( NOW( ) )
and work_order.order_create_time>#{start_time}
and work_order.order_create_time<#{end_time}
)AS work_order_deposit_price, ( select CASE WHEN SUM(CASE WHEN order_final_price is not null THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN order_final_price is not null THEN 1 ELSE 0 END) END FROM work_order WHERE order_is_delete='0'
and to_days(work_order.order_create_time) =to_days(now())
and YEARWEEK(date_format(work_order.order_create_time,'%Y-%m-%d')) = YEARWEEK(now())
and DATE_FORMAT(work_order.order_create_time, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' )
and YEAR(work_order.order_create_time) = YEAR( NOW( ) )
and work_order.order_create_time>#{start_time}
and work_order.order_create_time<#{end_time}
)AS work_order_complete_count, ( select CASE WHEN SUM(CASE WHEN order_final_price is not null THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN order_final_price is not null THEN order_price ELSE 0 END) END FROM work_order WHERE order_is_delete='0'
and to_days(work_order.order_create_time) =to_days(now())
and YEARWEEK(date_format(work_order.order_create_time,'%Y-%m-%d')) = YEARWEEK(now())
and DATE_FORMAT(work_order.order_create_time, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' )
and YEAR(work_order.order_create_time) = YEAR( NOW( ) )
and work_order.order_create_time>#{start_time}
and work_order.order_create_time<#{end_time}
)AS work_order_complete_price, ( select CASE WHEN SUM(CASE WHEN member_deposit_money is not null THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN member_deposit_money is not null THEN 1 ELSE 0 END) END FROM member WHERE member_is_delete='0'
and to_days(member.member_create_time) =to_days(now())
and YEARWEEK(date_format(member.member_create_time,'%Y-%m-%d')) = YEARWEEK(now())
and DATE_FORMAT(member.member_create_time, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' )
and YEAR(member.member_create_time) = YEAR( NOW( ) )
and member.member_create_time>#{start_time}
and member.member_create_time<#{end_time}
)AS worker_deposit_count, ( select CASE WHEN SUM(CASE WHEN member_deposit_money is not null THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN member_deposit_money is not null THEN member_deposit_money ELSE 0 END) END FROM member WHERE member_is_delete='0'
and to_days(member.member_create_time) =to_days(now())
and YEARWEEK(date_format(member.member_create_time,'%Y-%m-%d')) = YEARWEEK(now())
and DATE_FORMAT(member.member_create_time, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' )
and YEAR(member.member_create_time) = YEAR( NOW( ) )
and member.member_create_time>#{start_time}
and member.member_create_time<#{end_time}
)AS worker_deposit_price FROM system_setting

SELECT a.*,b.member_real_name,b.member_nick_name,b.member_account FROM ping_history AS a INNER JOIN member AS b WHERE
a.member_id=b.member_id and a.is_delete=0

	and b.member_real_name like concat('%',#{member_real_name},'%')


	and b.member_nick_name like concat('%',#{member_nick_name},'%')


	and b.member_account like concat('%',#{member_account},'%')


	and a.ping_type=#{ping_type}


	and a.create_time>#{start_time}


	and a.create_time<#{end_time}

order by a.create_time desc

